var Window  = require('./window.js'),
    fs      = require('fs'),
    U       = require('../utils.js'),
    Project = require('../project.js')
;

var Intro = module.exports = Window.extend({
  options: {
    chrome: false,
    url: '/intro.html'
  }
});

Intro.on('windowReady', function() {
  var self = this;
  self.$('button').click(function() {
    self.window.frame.openDialog({
        dirSelect: true,
        type: 'open'
      }, function(dontKnow, p) {
        self.application._project = Project.load(p[0]);
        self.application.project();
      })
  });
});