module.exports = Backbone.Router.extend({
  routes: {
    '': 'index'
  },

  initialize: function () {
    _.bindAll(this, 'index');
  },

  index: function () {
    var project = localStorage.get('project');
    project && (project = JEngineBuilder.Project.load(project));
    if (project) {
      Application.Project = project;
      Application.setActiveView(new JEngineBuilder.Views.Main).render();
    } else {
      this.navigate('projects/select', {trigger: true});
    }
  }
});