module.exports = Backbone.Router.extend({
  routes: {
    'projects/select': 'select',
    'projects/create': 'create'
  },

  initialize: function () {
    _.bindAll(this, 'select');
  },

  select: function () {
    Application.setActiveView(new JEngineBuilder.Views.Projects.Select()).render();
  },

  create: function() {
    Application.setActiveView(new JEngineBuilder.Views.Projects.Create()).render();
  }
});