module.exports = Backbone.View.extend({
  el: 'body',

  template: requireTemplate('./main'),

  name: 'Main',

  initialize: function() {
    _.bindAll(this, 'render');
    this.$el.data({view: this});
  },

  render: function() {
    this.$el.html(this.template());
    this.renderSceneView();
    this.renderBrowserView();
    return this;
  },

  renderSceneView: function() {
    this.sceneView || (this.sceneView = new JEngineBuilder.Views.Scene());
    this.sceneView.render();
    return this;
  },

  renderBrowserView: function() {
    this.browserView || (this.browserView = new JEngineBuilder.Views.Browser());
    this.browserView.render();
    return this;
  }
});