module.exports = Backbone.View.extend({
  name: 'Project Create',

  el: 'body',

  template: requireTemplate('projects/create'),

  events: {
    'submit form': 'create',
    'reset form': 'cancel'
  },

  initialize: function () {
    _.bindAll(this, 'render', 'create');
  },

  render: function () {
    this.$el.html(this.template());
    return this;
  },

  create: function () {
    e && e.preventDefault();
    JEningeBuilder.createProject({
      name: this.$el('#name').val(),
      path: this.$el('#location').val()
    })
    return this;
  },

  cancel: function(e) {
    e && e.preventDefault();
    Backbone.history.navigate('', {trigger: true});
    return this;
  }
});