module.exports = Backbone.View.extend({
  name: 'Project Select',

  el: 'body',

  template: requireTemplate('projects/select'),

  events: {
    'click #create-project': 'createProject',
    'click #open-project': 'openProject'
  },

  initialize: function () {
    _.bindAll(this, 'render', 'createProject', 'openProject');
    this.$el.data({view: this});
    this.recentProjects = localStorage.get('recentProjects');
  },

  render: function () {
    this.$el.html(this.template({recentProjects: this.recentProjects}));
    return this;
  },

  createProject: function() {
    Backbone.history.navigate('projects/create', {trigger: true});
    return this;
  },

  openProject: function() {
    return this;
  }
});