module.exports = Backbone.View.extend({
  el: '#scene',

  template: requireTemplate('./scene'),

  initialize: function() {
    _.bindAll(this, 'render');
    this.$el.data({view: this});
  },

  render: function() {
    this.$el.html(this.template());
    this.renderScene();
    return this;
  },

  renderScene: function() {
    var self = this;
    
    return self;
  }
})