var NOT_IMPLEMENTED_ERROR = function(method) {
  return new Error('Renderers must respond to "'+ method +'"!');
}
JEngine.Renderers.Core = JEngine.Utils.Obj.extend({
  constructor: function CoreRenderer(game) {
    this.setEngine(game);
  },

  setEngine: function setEngine(game) {
    this.game = game;
    this.setContext();
    return this;
  },

  setContext: function setContext() {
    throw NOT_IMPLEMENTED_ERROR('setContext');
  },

  draw: function draw(/* */) {
    throw NOT_IMPLEMENTED_ERROR('draw');
  },

  drawSquare: function drawSquare(/* */) {
    
  }
});