var NOT_IMPLEMENTED_ERROR = function(method) {
  return new Error('Renderers must respond to "'+ method +'"!');
}
var Renderer = module.exports = JEngine.Utils.Obj.extend({
  constructor: function(engine) {
    this.setEngine(engine);
  },

  setEngine: function(engine) {
    this.engine = engine;
    this.setContext();
    return this;
  },

  setContext: function() {
    throw NOT_IMPLEMENTED_ERROR('setContext');
  },

  draw: function(/* */) {
    throw NOT_IMPLEMENTED_ERROR('draw');
  },

  drawSquare: function(/* */) {
    
  }
});