var Renderer = module.exports = JEngine.Utils.Obj.extend({
  constructor: function(engine) {
    this.setEngine(engine);
  },

  setEngine: function(engine) {
    this.engine = engine;
    this.setContext();
    return this;
  },

  setContext: function() {
    throw new Error('Renderers must respond to setContext!');
  }
});