_.extend(JEngine.Utils, {
  supportsWebGL: function() {
    try {
      var canvas = document.createElement('CANVAS');
      if (canvas.getContext('webgl') || canvas.getContext('experimental-webgl')) {
        return true;
      }
    } catch (err) {
      return false;
    }
  },

  supportsCanvas: function() {
    try {
      var canvas = document.createElement('CANVAS');
      if (canvas.getContext('2d')) {
        return true;
      }
    } catch (err) {
      return false;
    }
  }
});