var Window  = require('./window.js'),
    fs      = require('fs'),
    U       = require('../utils.js'),
    _Project = require('../project.js')
;

var Project = module.exports = Window.extend({
  options: {
    chrome: false,
    url: '/project.html'
  }
});

Project.on('initialize', function() {
  this.project = this.application._project;
});

Project.on('windowReady', function() {
  var self = this;
  self.$('body').append(self.project.getOption('path'));
});