var winston = require('winston'),
  path = require('path'),
  jsonLogger = new winston.transports.File({filename: path.join(process.cwd(), 'debuglog.json'), colorize: true, handleExceptions: true}),
  prettyLogger = new winston.transports.File({filename: path.join(process.cwd(), 'debug.log'), json: false, prettyPrint: true, colorize: true, handleExceptions: true})
;

jsonLogger.name = 'debug-json';
prettyLogger.name = 'debug-pretty';

module.exports = new (winston.Logger)({
  transports: [jsonLogger, prettyLogger]
});